import React, { useState, useEffect } from "react";
import movie_poster from "../media/movie_poster.jpg";

const Home = () => {
    const [pisctureMovie, setPictureMovie] = useState("");

    let image = `https://image.tmdb.org/t/p/w400`;
    useEffect(() => {
        axios.get(`https://api.themoviedb.org/3/discover/movie?&api_key=721e6d30945061e57c30f65c44877cf8`)
            .then(response => {
                console.log(response.data.results);
                setPictureMovie(response.data.results);
            })
    }, [pisctureMovie])


    return (
        <div className="home">
            <img src={pisctureMovie.poster_path ? `${image}/${pisctureMovie.poster_path}` : movie_poster} alt="picture home" />
        </div>
    )
}
export default Home;
