import React, { useState, useEffect } from "react";
import axios from "axios";
import coming_soon from "../media/coming_soon.png";

const Search = () => {
    let image = "https://image.tmdb.org/t/p/w400";
    const [searchMovies, setSearchMovies] = useState(null);
    const [query, setQuery] = useState("");
    const [searchTypes, setSearchTypes] = useState("movie")
    console.log(query);
    useEffect(() => {
        axios.get(`https://api.themoviedb.org/3/search/${searchTypes}?&api_key=721e6d30945061e57c30f65c44877cf8&query=${query}`)
            .then((response) => {
                console.log("mes films2", response.data.results);
                setSearchMovies(response.data.results);
            })

    }, [query])
    return searchMovies !== null ? (
        <div>
            <label htmlFor="keyword">Mots-clés </label>
            <input value={query} id="keyword" type="search" placeholder="Filtrer par mots-clés"
                onChange={(e) => setQuery(e.target.value)} />
            <label htmlFor="person">chercher un acteur</label>
            <input type="radio" id="person" name="2" onClick={() => setSearchTypes("person")} />

            <div id="searchMovie">
                {
                    searchMovies.map((item) => {
                        return (

                            <div>
                                <h1>{item.original_title || item.original_name}</h1>
                                <img src={item.poster_path ? `${image}${item.poster_path}` : coming_soon} alt="picture" />
                                <p>{item.release_date}</p>
                                <p>Moyenne des vote: {item.vote_average !== 0 ? item.vote_average + "/" + "10" : "Non renseigné"}</p>
                            </div>

                        )

                    })
                }
            </div>

        </div>
    ) : (
            <div>
                <label htmlFor="keyword">Mots-clés </label>
                <input value={query} id="keyword" type="search" placeholder="Filtrer par mots-clés"
                    onChange={(e) => setQuery(e.target.value)} />
            </div>
        )
}

export default Search;
