import React, { useState, useEffect } from "react";
import axios from "axios";

const Search = () => {
    const [searchMovies, setSearchMovies] = useState(null);
    const [query, setQuery] = useState(null);
    useEffect(() => {
        axios.get("https://api.themoviedb.org/3/search/movie?&api_key=721e6d30945061e57c30f65c44877cf8&query=${query}")
        .then((response) => {
            console.log("mes films2", response.data.results);
            setSearchMovies(response.data.results);
        })

    })
    return searchMovies !== null & query !== null?(
        <div>
            <label htmlFor="keyword">Mots-clés </label>
            <input value={query} id="keyword" type="search" placeholder="Filtrer par mots-clés"
                onChange={(e) => setQuery(e.target.value)} />

        </div>
    ):(
        <p>Pas de data</p>
    )
}

export default Search;
