import axios from 'axios';
import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [movies, setMovies] = useState(null);
  const imagesApi = "https://api.themoviedb.org/3/movie/30/images?api_key=721e6d30945061e57c30f65c44877cf8";
  let image = "https://image.tmdb.org/t/p/w500"
  const categieries = "movie";
  useEffect(() => {
    axios.get(`https://api.themoviedb.org/3/discover/${categieries}?&api_key=721e6d30945061e57c30f65c44877cf8`)
      .then(response => {
        console.log(response.data.results);
        setMovies(response.data.results);
      })
  }, [])
  return movies !== null ? (
    <div className="App">
      <ul>
        {
          movies.map((item) => {
            return (
              <div>
                <h1 key={item.id}>{item.original_title || item.original_name}</h1>
                <p>{item.overview}</p>
                <img src= {`${image}/${item.poster_path}`} alt="picture" />
              </div>

            )
          })
        }
      </ul>

    </div>
  ) : (
      <p>Pas de data</p>
    )
}

export default App;
