import axios from 'axios';
import Modal from "react-modal";
import React, { useEffect, useState } from 'react';
import coming_soon from "../media/coming_soon.png";


function MovieList() {

  const [movies, setMovies] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [categories, setCategories] = useState("movie");
  const [types, setTypes] = useState("discover");
  const [language, setLanguage] = useState("");
  const [sortBy, setSortBy] = useState("popularity");
  const [includeAdult, setIncludeAdult] = useState(false);
  const [includeVideo, setIncludeVideo] = useState(false);
  const [page, setPage] = useState(1); 
  console.log(page)
  const [genre, setGenre] = useState("");
  const [year, setyear] = useState(2020);


  let image = "https://image.tmdb.org/t/p/w400";
  useEffect(() => {
    axios.get(`https://api.themoviedb.org/3/${types}/${categories}?&api_key=721e6d30945061e57c30f65c44877cf8&with_genres=${genre}&primary_release_year=${year}&language=${language}&sort_by=${sortBy}&include_adult=${includeAdult}&include_video=${includeVideo}&page=${page}`)
      .then(response => {
        console.log(response.data.results);
        setMovies(response.data.results);
      })
  }, [page,categories, genre])


  return movies !== null && categories !== null ? (
    <div className="movie_list">
      <div className="filter">
        <h1>Filter</h1>
        <p>filtrer par type</p>
        <label htmlFor="movie">Films</label>
        <input type="radio" id="movie" name="1" onClick={()=>setCategories("movie")}/>
        <label htmlFor="tv">Télivision</label>
        <input type="radio" id="tv" name="1"onClick={()=>setCategories("tv")}/>
        <p>Chercher des films par catégories</p>
        <label htmlFor="action">Action</label>
        <input type="radio" id="action" name="1"onClick={()=>setGenre(28) + setPage(1)}/>
        <label htmlFor="adventure">Aventure</label>
        <input type="radio" id="adventure" name="1"onClick={()=>setGenre(12)}/>
      </div>
      <div>
        <button type="button" onClick={() => setPage(page !== 0 && page < 31 ? page - 1 : 1)}>Page précédente</button>
        <button type="button" onClick={() => setPage(page !== 0 && page < 31 ? page + 1 : 1)} >Page Suivante</button>
      </div>
      <ul>
        {
          movies.map((item) => {
            return (
              <div className="posts">

                <h1 key={item.id}>{item.original_title || item.original_name}</h1>
                <img src={item.poster_path ? `${image}/${item.poster_path}`: coming_soon} alt="picture" />
                <p>{item.release_date}</p>
                <p>Moyenne des vote: {item.vote_average !== 0 ? item.vote_average + "/" + "10" : "Non renseigné"}</p>
                <button onClick={() => setModalIsOpen(true)}>Infos++</button>
                <Modal isOpen={modalIsOpen}
                  ariaHideApp={false}
                  style={{
                    content: {
                      backgroundColor: "black",
                      color: "wheat",
                      width: "80%",
                      textAlign: "center",
                      fontSize: "x-large",
                      position: "absolute",
                      left: "50%",
                      top: "50%",
                      transform: "translate(-50%, -50%)",
                      border: "solid 1px black",
                    },
                  }}>
                  <p id = {item.id}>{item.overview}</p>
                  <img id = {item.id} src={`${image}/${item.backdrop_path}`} alt="picture movies" />

                </Modal>
              </div>

            )
          })
        }
      </ul>

    </div>
  ) : (
      <div className="filter">
        <p>Faites une autre recherche</p>
        <h1>Filter</h1>
        <label htmlFor="movie">Films</label>
        <input type="radio" id="movie" name="1" onClick={()=>setCategories("movie")}/>
        <label htmlFor="tv">Télivision</label>
        <input type="radio" id="tv" name="1"onClick={()=>setCategories("tv")}/>
        <input type="radio" id="movie" name="1"/>
      </div>
    )
}

export default MovieList;
