import React, { useState, useEffect } from "react";
import axios from "axios";

const Search = () => {
    const [searchMovies, setSearchMovies] = useState(null);
    const [query, setQuery] = useState("");
    useEffect(() => {
        axios.get("https://api.themoviedb.org/3/movie?&api_key=721e6d30945061e57c30f65c44877cf8&query=${query}");

    })
    return (
        <div>
            <label htmlFor="keyword">Mots-clés </label>
            <input value={query} id="keyword" type="search" placeholder="Filtrer par mots-clés"
                onChange={(e) => setQuery(e.target.value)} />
        </div>
    )
}

export default Search;
