import axios from 'axios';
import Modal from "react-modal";
import React, { useEffect, useState } from 'react';
import './App.css';

function App() {
  const [movies, setMovies] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  let image = "https://image.tmdb.org/t/p/w500";
  const categieries = "tv";
  useEffect(() => {
    axios.get(`https://api.themoviedb.org/3/discover/${categieries}?&api_key=721e6d30945061e57c30f65c44877cf8`)
      .then(response => {
        console.log(response.data.results);
        setMovies(response.data.results);
      })
  }, [])
  return movies !== null ? (
    <div className="App">
      {
        movies.map((element) => {
          <Modal isOpen={modalIsOpen}
            ariaHideApp={false}
            style={{
              content: {
                backgroundColor: "#FDF1D8",
                width: "80%",
                textAlign: "center",
                fontSize: "x-large",
                position: "absolute",
                left: "50%",
                top: "50%",
                transform: "translate(-50%, -50%)",
                border: "solid 1px black",
              },
            }}>
            <p>{element.overview}</p>
            <img src={`${image}/${element.backdrop_path}`} alt="picture movies" />

          </Modal>
        })

      }

      <ul>
        {
          movies.map((item) => {
            return (
              <div className="posts">
                <h1 key={item.id}>{item.original_title || item.original_name}</h1>
                <p className="overview">{item.overview}</p>
                <img src={`${image}/${item.poster_path}`} alt="picture" />
                <button onClick={() => setModalIsOpen(true)}>Infos++</button>

              </div>

            )
          })
        }
      </ul>

    </div>
  ) : (
      <p>Pas de data</p>
    )
}

export default App;
